document.addEventListener('DOMContentLoaded', () => {
  // Initialize AOS
  AOS.init({
    duration: 1000,
    easing: 'ease-in-out',
    once: true,
    mirror: false
  });

  // Initialize Typed.js
  if (document.querySelector('.typed')) {
    let typed_strings = document.querySelector('.typed').getAttribute('data-typed-items');
    typed_strings = typed_strings.split(',');
    new Typed('.typed', {
      strings: typed_strings,
      loop: true,
      typeSpeed: 100,
      backSpeed: 50,
      backDelay: 2000
    });
  }

  // Project details data
  const projectsData = {
    ecommerce: {
      title: "Flight Reservation System",
      category: "Web Development",
      description: "The Flight Reservation System is a web-based platform that enables users to search for available flights, make reservations, and manage their bookings efficiently. The application simulates a real-world airline ticketing system with a user-friendly interface and role-based access. Users can book flights based on their preferences (source, destination, date) and manage their tickets with options to cancel or update bookings. The backend handles session management, data validation, and communication with the MySQL database. The application is designed using the MVC architecture to ensure scalability and maintainability.",
      technologies: ["HTML5", "CSS3", "JavaScript", "MYSQL", "Apache Tomcat v9", "Eclipse IDE (Java EE)"],
      github: "https://github.com/Varundeep09/Flight_Reservation",
      live: "https://ecommerce-demo.example.com",
      image: "data:image/jpeg;base64,"
    },
    portfolio: {
      title: "Tune-In-Local",
      category: "Web Application",
      description: "Tune In Local is a web application designed to connect independent music artists, enabling them to collaborate, form groups, and find opportunities for gigs or jam sessions. The platform supports artist registration with profile details and experience, group formation for collaborations, and a public feed for posting or joining events. Whether solo performers or existing bands, users can reach out to others, create or join music groups, and respond to gig invitations. The app encourages community-driven collaboration and event participation in the local music scene. Developed using clean MVC architecture, it is optimized for deployment using Apache Tomcat.",
      technologies: ["HTML5", "CSS3", "JavaScript", "Java", "JSP", "Servlet"],
      github: "https://github.com/Varundeep09/Tune-In-Local",
      live: "https://portfolio-demo.example.com",
      image: "data:image/jpeg;base64," },
    blog: {
      title: " My PortFlio",
      category: "Only Front-End",
      description: "A feature-rThe Personal Portfolio Website is a responsive and interactive web application built to showcase my skills, projects, resume, and contact information. It serves as a digital identity, allowing visitors and recruiters to learn about my background, technical expertise, and achievements in a professional and engaging format. The site includes sections like About Me, Projects, Skills, Resume Download, and a Contact Form. The design emphasizes simplicity, clarity, and performance across all devices.ich blog platform with content management system, user authentication, and commenting system.",
      technologies: ["HTML5", "CSS3", "JavaScript", "Forms", "Vercel/Netlify"],
      github: "https://github.com/yourusername/blog-platform",
      live: "https://blog-demo.example.com",
      image: "data:image/jpeg;base64,"
    }
  };

  // Initialize project modal functionality
  const projectModal = new bootstrap.Modal(document.getElementById('projectModal'));
  
  // Add click event listeners to project cards
  document.querySelectorAll('.view-project').forEach(button => {
    button.addEventListener('click', (e) => {
      const projectCard = e.target.closest('.project-card');
      const projectId = projectCard.dataset.project;
      const project = projectsData[projectId];

      // Update modal content
      document.querySelector('.project-modal-image').src = project.image;
      document.querySelector('.project-modal-title').textContent = project.title;
      document.querySelector('.project-modal-category').textContent = project.category;
      document.querySelector('.project-modal-description').textContent = project.description;
      
      // Update technologies list
      const techList = document.querySelector('.project-modal-technologies');
      techList.innerHTML = project.technologies.map(tech => `<li>${tech}</li>`).join('');
      
      // Update links
      document.querySelector('.project-modal-github').href = project.github;
      document.querySelector('.project-modal-live').href = project.live;

      // Show modal
      projectModal.show();
    });
  });

  // Navbar scroll behavior
  const navbar = document.querySelector('.navbar');
  window.addEventListener('scroll', () => {
    if (window.scrollY > 50) {
      navbar.classList.add('scrolled');
    } else {
      navbar.classList.remove('scrolled');
    }
  });

  // Smooth scrolling for navigation links
  document.querySelectorAll('a[href^="#"]').forEach(anchor => {
    anchor.addEventListener('click', function(e) {
      e.preventDefault();
      const target = document.querySelector(this.getAttribute('href'));
      if (target) {
        window.scrollTo({
          top: target.offsetTop - 70,
          behavior: 'smooth'
        });
      }
    });
  });

  // Active navigation link on scroll
  const sections = document.querySelectorAll('section');
  const navLinks = document.querySelectorAll('.nav-link');

  window.addEventListener('scroll', () => {
    let current = '';
    sections.forEach(section => {
      const sectionTop = section.offsetTop;
      const sectionHeight = section.clientHeight;
      if (window.scrollY >= sectionTop - 150) {
        current = section.getAttribute('id');
      }
    });

    navLinks.forEach(link => {
      link.classList.remove('active');
      if (link.getAttribute('href').slice(1) === current) {
        link.classList.add('active');
      }
    });
  });

  // Skills animation
  const skillsSection = document.querySelector('#skills');
  if (skillsSection) {
    const progressBars = skillsSection.querySelectorAll('.progress-bar');
    const observer = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          progressBars.forEach(bar => {
            bar.style.width = bar.getAttribute('style').split(':')[1];
          });
        }
      });
    });
    observer.observe(skillsSection);
  }

  // Contact form handling
  const contactForm = document.getElementById('contactForm');
  if (contactForm) {
    contactForm.addEventListener('submit', (e) => {
      e.preventDefault();
      // Add your form submission logic here
      alert('Thank you for your message! I will get back to you soon.');
      contactForm.reset();
    });
  }
});